const settingBtn=document.querySelectorAll(".menuAd__Btn_options"),dropDawnMenu=document.querySelector(".dropDownMenu"),activeDropMenu=e=>{if(dropDawnMenu.classList.contains("dropDownMenu_active"))dropDawnMenu.classList.remove("dropDownMenu_active");else{const{pageY:n,pageX:t}=e;dropDawnMenu.classList.add("dropDownMenu_active"),dropDawnMenu.style.left=t-200+"px",dropDawnMenu.style.top=n-160+"px"}};settingBtn.forEach((e=>{e.addEventListener("click",activeDropMenu)}));export default activeDropMenu;